import logo from './logo.svg';
import './App.css';
// components
import FormProvider from './components/hook-form/FormProvider';
import RHFTextField from './components/hook-form/RHFTextField';
import { LoadingButton } from '@mui/lab';
// form
import * as Yup from 'yup';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
// @mui
import { styled } from '@mui/material/styles';
import { Box, IconButton, Container, Typography, Stack, Card, CardHeader, CardContent} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
// notistack
import { useSnackbar } from 'notistack';
// Axios
import _axios from 'axios';

// ----------------------------------------------------------------------

// const RootStyle = styled('div')(({ theme }) => ({
//   [theme.breakpoints.up('md')]: {
//     display: 'flex'
//   }
// }));

const ContentStyle = styled('div')(({ theme }) => ({
  maxWidth: 700,
  margin: 'auto',
  // minHeight: '100vh',
  // display: 'flex',
  // justifyContent: 'center',
  // flexDirection: 'column',
  padding: theme.spacing(2, 0)
}));

// ----------------------------------------------------------------------
export async function getStaticProps() {
  return {
    props: {}
  };
}

function App(props) {
  const { enqueueSnackbar } = useSnackbar();

  const FormSchema = Yup.object().shape({
    locale: Yup.string()
  });

  const methods = useForm({
    resolver: yupResolver(FormSchema),
    // defaultValues
  });

  const {
    handleSubmit,
    formState: { isSubmitting }
  } = methods;

  const onSubmit = async (data, message) => {
    console.log(data)
    await _axios
      .get(`https://api.openweathermap.org/data/2.5/weather`, {
        params: {
          q: data.country,
          appid: process.env.REACT_APP_API_KEY,
        },
      })
      .then((res) => {
        console.log(res.data)
        enqueueSnackbar(res.data.name, { variant: 'success' });
      })
      .catch((error) => {
        enqueueSnackbar(error.message, {
          variant: 'error'
        });
      });
  };

  return (
    <ContentStyle>
      <FormProvider methods={methods} onSubmit={handleSubmit(onSubmit)}>
      <Card style={{ border: "none", boxShadow: "none" }}>
        <CardHeader
          action={
            <IconButton sx={{ p: 4 }} aria-label="settings" type="submit" id="submitButton">
              <SearchIcon />
            </IconButton>
          }
          title={
            <RHFTextField
              sx={{ mr: 2 }}
              name="country"
              label="country"
            />
          }
        />
      </Card>

        {/* <LoadingButton
          id="submitButton"
          size="large"
          type="submit"
          variant="contained"
          loading={isSubmitting}
          sx={{ m: 'auto' }}
        >
        </LoadingButton> */}

      </FormProvider>
      <Box sx={{ mt: 1, mb: 1 }}>
        <Card>
          <CardHeader title="Weather" sx={{ p: 2 }} />
          <CardContent>

          </CardContent>
        </Card>
      </Box>
    </ContentStyle>
  );
}

export default App;
