  //     ...(await serverSideTranslations(locale, [
  //       'language',
  //       'nav',
  //       'notistack',
  //       'breadcrumbs'
  //     ]))
  //   }
  // };
}

function App() {
  const { enqueueSnackbar } = useSnackbar();

  const {
    handleSubmit,
    formState: { isSubmitting }
  } = methods;

  const onSubmit = async (data, message) => {
    await _axios
      .get(`https://api.openweathermap.org/data/2.5/weather`, 
        { 
          appid: 'a0fd2100335cee24326e9549cb7e01e6',
          q: 'London'
        }
      )
      .then((res) => {
        console.log(res)
        // if (message != false) {
        //   enqueueSnackbar(res.data.message, { variant: 'success' });
        // }
      })
      .catch((error) => {
        enqueueSnackbar(t('language:fallback_default_lang'), {
          variant: 'error'
        });
      });
  };

  return (
    <Page title="Todayâ€™s Weather">
      <FormProvider methods={methods} onSubmit={handleSubmit(onSubmit)}>
        <Card sx={{ p: 3, mx: 'auto', maxWidth: 720 }} variant="outlined">
          <CardHeader
            avatar={<TranslateIcon />}
            title={t('language:select_language')}
            sx={{ mb: 3, p: 0 }}
          />
          <CardContent sx={{ p: 0, '&:last-child': { paddingBottom: 0 } }}>
            <Stack spacing={2}>
              <RHFSelect name="locale" label={t('language:language')}>
                {locales.map((option) => (
                  <MenuItem
                    key={option.value}
                    value={option.value}
                    sx={{
                      mx: 1,
                      my: 0.5,
                      borderRadius: 0.75,
                      typography: 'body2',
                      textTransform: 'capitalize'
                    }}
                  >
                    {t(`language:${option.label}`)}
                  </MenuItem>
                ))}
              </RHFSelect>
              <Typography variant="body2" color="red">
                {t('language:note')}
              </Typography>
            </Stack>
          </CardContent>
        </Card>
        <Stack alignItems="center" sx={{ mt: 3 }}>
          <LoadingButton
            fullWidth
            size="large"
            type="submit"
            variant="contained"
            loading={isSubmitting}
            sx={{ maxWidth: '300px', m: 'auto' }}
          >
            {t('language:update')}
          </LoadingButton>
        </Stack>
      </FormProvider>
    </Page>
  );
}

export default App;
