import logo from './logo.svg';
import './App.css';
// components
import Page from './components/Page';
import FormProvider from './components/hook-form/FormProvider';
import { LoadingButton } from '@mui/lab';
// form
import * as Yup from 'yup';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
// @mui
import { Typography, Stack, MenuItem, Card, CardHeader, CardContent} from '@mui/material';
import TranslateIcon from '@mui/icons-material/Translate';
// notistack
import { useSnackbar } from 'notistack';
// Axios
import _axios from 'axios';

// ----------------------------------------------------------------------
export async function getStaticProps() {
  return {
    props: {}
  };
}

function App(props) {
  const { enqueueSnackbar } = useSnackbar();

  const FormSchema = Yup.object().shape({
    locale: Yup.string()
  });

  const methods = useForm({
    resolver: yupResolver(FormSchema),
    // defaultValues
  });

  const {
    handleSubmit,
    formState: { isSubmitting }
  } = methods;

  const onSubmit = async (data, message) => {
    await _axios
      .get(`https://api.openweathermap.org/data/2.5/weather`, {
        params: {
          q: 'Penang',
          appid: process.env.REACT_APP_API_KEY,
        },
      })
      .then((res) => {
        console.log(res.data)
        enqueueSnackbar(res.data.name, { variant: 'success' });
      })
      .catch((error) => {
        enqueueSnackbar(error.message, {
          variant: 'error'
        });
      });
  };

  return (
    // <div className="App">
    //   <header className="App-header">

    //   </header>
    //   <body>
        <FormProvider methods={methods} onSubmit={handleSubmit(onSubmit)}>
          <Card sx={{ p: 3, mx: 'auto', maxWidth: 720 }} variant="outlined">
             <CardHeader
            avatar={<TranslateIcon />}
            // title={t('language:select_language')}
            sx={{ mb: 3, p: 0 }}
          />
            <CardContent sx={{ p: 0, '&:last-child': { paddingBottom: 0 } }}>
              <Stack spacing={2}>

              </Stack>
            </CardContent>
          </Card>
          <Stack alignItems="center" sx={{ mt: 3 }}>
            <LoadingButton
              fullWidth
              size="large"
              type="submit"
              variant="contained"
              loading={isSubmitting}
              sx={{ maxWidth: '300px', m: 'auto' }}
            >
              search
              {/* {t('language:update')} */}
            </LoadingButton>
          </Stack>
        </FormProvider>
    //   </body>
    // </div>

  );
}

export default App;
