import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';
// components
import FormProvider from './components/hook-form/FormProvider';
import RHFTextField from './components/hook-form/RHFTextField';
import { LoadingButton } from '@mui/lab';
// form
import * as Yup from 'yup';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
// @mui
import { styled } from '@mui/material/styles';
import { Paper, Box, IconButton, Container, Typography, Stack, Card, CardHeader, CardContent} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
// notistack
import { useSnackbar } from 'notistack';
// Axios
import _axios from 'axios';
// ----------------------------------------------------------------------

// const RootStyle = styled('div')(({ theme }) => ({
//   [theme.breakpoints.up('md')]: {
//     display: 'flex'
//   }
// }));

const ContentStyle = styled('div')(({ theme }) => ({
  maxWidth: 700,
  margin: 'auto',
  // minHeight: '100vh',
  // display: 'flex',
  // justifyContent: 'center',
  // flexDirection: 'column',
  padding: theme.spacing(2, 0)
}));

// ----------------------------------------------------------------------
export async function getStaticProps() {
  return {
    props: {}
  };
}

export default function App(props) {
  const { enqueueSnackbar } = useSnackbar();
  const [weather, setWeather] = useState([]);
  const [weatherList, setWeatherList] = useState([]);

  const FormSchema = Yup.object().shape({
    locale: Yup.string()
  });

  const methods = useForm({
    resolver: yupResolver(FormSchema),
    // defaultValues
  });

  const {
    handleSubmit,
    formState: { isSubmitting }
  } = methods;

  const onSubmit = async (data) => {
    console.log(data)
    await _axios
      .get(`https://api.openweathermap.org/data/2.5/weather`, {
        params: {
          q: data.country,
          appid: process.env.REACT_APP_API_KEY,
        },
      })
      .then((res) => {
        console.log(res.data);
        setWeather([res.data]);
        // setWeather({ [res.data.data.login]: res.data.data });
      })
      .catch((error) => {
        enqueueSnackbar(error.message, {
          variant: 'error'
        });
      });
  };

  return (
    <ContentStyle>
      <FormProvider methods={methods} onSubmit={handleSubmit(onSubmit)}>
        <Card style={{ border: "none", boxShadow: "none" }}>
          <CardHeader
            sx={{ p:0 }}
            action={
              <IconButton sx={{ p: 3 }} aria-label="settings" type="submit" id="submitButton">
                <SearchIcon />
              </IconButton>
            }
            title={
              <RHFTextField
                InputProps={{ sx: { borderRadius: 5 } }}
                sx={{ 
                  mr: 5
                }}
                name="country"
                label="Country"
              />
            }
          />
        </Card>
      </FormProvider>
      <Box sx={{ my: 5}}>
        {weather && <Card 
          style={{ border: "none", boxShadow: "none", backgroundColor: ""}}
          sx= {{ borderRadius: 5 }}
        >
          <CardHeader title="Today's Weather" sx={{ p: 2 }} />
          <CardContent>
          <Typography
            variant="body2"
            sx={{
              maxWidth: 260,
              overflow: 'hidden',
              whiteSpace: 'nowrap',
              color: 'text.disabled',
              textOverflow: 'ellipsis'
            }}
          >
            {weather.main.temp}
          </Typography>
          </CardContent>
        </Card>}
      </Box>
    </ContentStyle>

  );
}
