import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';
// components
import FormProvider from './components/hook-form/FormProvider';
import RHFTextField from './components/hook-form/RHFTextField';
import { LoadingButton } from '@mui/lab';
// form
import * as Yup from 'yup';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
// @mui
import { styled } from '@mui/material/styles';
import { Grid, Box, IconButton, Container, Typography, Stack, Card, CardHeader, CardContent} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import { useTheme } from '@mui/material/styles';
import useMediaQuery from '@mui/material/useMediaQuery';
// notistack
import { useSnackbar } from 'notistack';
// Axios
import _axios from 'axios';
// ----------------------------------------------------------------------

// const RootStyle = styled('div')(({ theme }) => ({
//   [theme.breakpoints.up('md')]: {
//     display: 'flex'
//   }
// }));

const ContentStyle = styled('div')(({ theme }) => ({
  maxWidth: 700,
  margin: 'auto',
  padding: theme.spacing(4)
}));

// ----------------------------------------------------------------------
export async function getStaticProps() {
  return {
    props: {}
  };
}

export default function App(props) {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down('sm'));
  const { enqueueSnackbar } = useSnackbar();
  const [weather, setWeather] = useState();
  const [weatherList, setWeatherList] = useState([]);

  const FormSchema = Yup.object().shape({
    locale: Yup.string()
  });

  const methods = useForm({
    resolver: yupResolver(FormSchema),
    // defaultValues
  });

  const {
    handleSubmit,
    formState: { isSubmitting }
  } = methods;

  const onSubmit = async (data) => {
    console.log(data)
    await _axios
      .get(`https://api.openweathermap.org/data/2.5/weather`, {
        params: {
          units: "metric",
          q: data.country,
          appid: process.env.REACT_APP_API_KEY,
        },
      })
      .then((res) => {
        console.log(res.data);
        setWeather(res.data);
      })
      .catch((error) => {
        enqueueSnackbar(error.message, {
          variant: 'error'
        });
      });
  };

  return (
    <ContentStyle>
      <FormProvider methods={methods} onSubmit={handleSubmit(onSubmit)}>
        <Card style={{ border: "none", boxShadow: "none" }}>
          <CardHeader
            sx={{ p:0 }}
            action={
              <IconButton sx={{ p: 3 }} aria-label="settings" type="submit" id="submitButton">
                <SearchIcon />
              </IconButton>
            }
            title={
              <RHFTextField
                InputProps={{ sx: { borderRadius: 5 } }}
                sx={{ 
                  mr: 5
                }}
                name="country"
                label="Country"
              />
            }
          />
        </Card>
      </FormProvider>
      <Box sx={{ my: 5}}>
         <Card 
          // style={{ border: "none", boxShadow: "none", backgroundColor: ""}}
          sx= {{ borderRadius: 5 }}
        >
          <CardContent>
          {weather && <>
            {isSmallScreen ? 
              <>
                <Grid container>
                  <Grid item xs={6}>
                    <Typography variant="body2" style={{ fontWeight: 'bold' }}>
                      Today's Weather
                    </Typography>
                    <Typography variant="h2" style={{ fontWeight: 'bold' }}>
                      {Number(weather.main.temp.toFixed(0))}°
                    </Typography>
                    <Typography variant="body2" style={{ fontWeight: 'bold' }}>
                      H:{Number(weather.main.temp_max.toFixed(0))}° L:{Number(weather.main.temp_min.toFixed(0))}°
                    </Typography>
                    <Typography variant="body2" style={{ fontWeight: 'bold' }}>
                      {weather.name}, {weather.sys.country}
                    </Typography>
                  </Grid>
                  <Grid item xs={6} sx={{ mt: 'auto' }}>
                    <Stack spacing={1}>
                      <Typography variant="body2" align="right"> 
                        Coulds: {weather.coulds}%
                      </Typography>
                      <Typography variant="body2" align="right">
                        Humidity: {weather.main.humidity}%
                      </Typography>
                      <Typography variant="body2" align="right">
                        {weather.main.humidity}
                      </Typography>
                    </Stack>
                  </Grid>
                </Grid>
              </>:<>
                <Typography variant="body1" sx={{ fontWeight: 'bold' }}>
                  Today's Weather
                </Typography>
                <Typography variant="h1" sx={{ fontWeight: 'bold' }}>
                  {Number(weather.main.temp.toFixed(0))}°
                </Typography>
                <Typography variant="body1" sx={{ fontWeight: 'bold' }}>
                  H:{Number(weather.main.temp_max.toFixed(0))}° L:{Number(weather.main.temp_min.toFixed(0))}°
                </Typography>
                <Grid container >
                  <Grid item xs={3}>
                    <Typography sx={{ fontWeight: 'bold', typography: { sm: 'body1', xs: 'body2' } }}>
                      {weather.name}, {weather.sys.country}
                    </Typography>
                  </Grid>
                  <Grid item xs={3}>
                    <Typography sx={{ margin: 'auto', typography: { sm: 'body1', xs: 'body2' } }}>
                      {weather.main.temp_max}
                    </Typography>
                  </Grid>
                  <Grid item xs={3}>
                    <Typography sx={{ margin: 'auto', typography: { sm: 'body1', xs: 'body2' } }}>
                      Humidity: {weather.main.humidity}%
                    </Typography>
                  </Grid>
                  <Grid item xs={3}>
                    <Typography sx={{ margin: 'auto', typography: { sm: 'body1', xs: 'body2' } }}> 
                      Coulds: {weather.coulds}%
                    </Typography>
                  </Grid>
                </Grid>
              </>
            }
          </>}

            <Card sx= {{ borderRadius: 5, my: 5 }}>
              <CardHeader title={
                <Typography sx={{ sm: 'body1', xs: 'body2' }}>
                  Search History
                </Typography>
              }>
                Search History
              </CardHeader>
              <CardContent>
                <Stack spacig={2}>

                  <Card sx= {{ borderRadius: 5 }}>
                    <CardContent>
                      <></>
                    </CardContent>
                  </Card>

                </Stack>
              </CardContent>
            </Card>
          </CardContent>
        </Card>
      </Box>
    </ContentStyle>

  );
}
